// Fun√ß√£o para "aplanar" objetos aninhados para o formato 'dot notation'
function flattenObject(obj, parentKey = '') {
  let result = {};
  for (let key in obj) {
    if (Object.prototype.hasOwnProperty.call(obj, key)) {
      const newKey = parentKey ? `${parentKey}.${key}` : key;
      const value = obj[key];
      if (typeof value === 'object' && value !== null && !Array.isArray(value) && !(value instanceof RegExp)) {
        result = { ...result, ...flattenObject(value, newKey) };
      } else {
        result[newKey] = value;
      }
    }
  }
  return result;
}

// Recebe o item de input do n8n
const inputData = $input.first().json.output;
const processedData = [];

const data = { message: { content: inputData } };
if (data.message && data.message.content) {
  try {
    let contentStr = data.message.content.trim();
    // ‚úÖ Remove marca√ß√£o de Markdown (```json ... ```)
    if (contentStr.startsWith('```')) {
      contentStr = contentStr.replace(/```json|```/gi, '').trim();
    }

    // ‚úÖ Lista para guardar as express√µes regulares antes de processar o JSON
    const regexList = [];
    const processedContent = contentStr.replace(/\/([^/]+)\//g, (match, p1) => {
      const placeholder = `__regex_${regexList.length}__`;
      regexList.push(p1);
      return `"${placeholder}"`;
    });

    // ‚úÖ Processa a string como um JSON normal
    let parsedObject = JSON.parse(processedContent);

    // ‚úÖ Substitui os placeholders de volta por objetos RegExp
    for (const key in parsedObject) {
      if (typeof parsedObject[key] === 'string' && parsedObject[key].startsWith('__regex_')) {
        const index = parseInt(parsedObject[key].replace('__regex_', ''), 10);
        if (!isNaN(index) && regexList[index]) {
          parsedObject[key] = new RegExp(regexList[index]);
        }
      }
    }

    // ‚úÖ Aplanar o objeto para o formato 'dot notation'
    let finalObject = flattenObject(parsedObject);

    // ‚úÖ Mover UF de periodoDespesa para deputado se existir
    if (finalObject['periodoDespesa.uf']) {
      finalObject['deputado.uf'] = finalObject['periodoDespesa.uf'];
      delete finalObject['periodoDespesa.uf'];
    }

    // ‚úÖ Remove campos nulos, vazios ou undefined
    Object.keys(finalObject).forEach(key => {
      const value = finalObject[key];
      if (
        value === null ||
        value === '' ||
        value === undefined
      ) {
        delete finalObject[key];
      }
    });

    // ‚úÖ Ajuste especial para o campo 'deputado.nome'
    if (finalObject['deputado.nome'] !== undefined) {
      let nomeValue = finalObject['deputado.nome'];
      if (nomeValue === 'deputado') {
        delete finalObject['deputado.nome'];
      } else {
        let regexValue = '';
        if (typeof nomeValue === 'string') {
          regexValue = nomeValue;
        } else if (nomeValue instanceof RegExp) {
          regexValue = nomeValue.source;
        }

        finalObject['deputado.nome'] = {
          $regex: regexValue,
          $options: 'i'
        };
      }
    }

    // ‚úÖ Adiciona o objeto final em um novo objeto na lista de retorno
    processedData.push({ json: finalObject });
  } catch (error) {
    console.log('Erro ao processar content:', error);
    processedData.push({ json: { content: inputData, error: 'Erro de processamento' } });
  }
}

return processedData;

