// Fun√ß√£o para formatar valores monet√°rios
function formatarValor(valor) {
  return new Intl.NumberFormat('pt-BR', {
    style: 'currency',
    currency: 'BRL'
  }).format(valor);
}

// Fun√ß√£o para formatar n√∫meros
function formatarNumero(numero) {
  return new Intl.NumberFormat('pt-BR').format(numero);
}

// Fun√ß√£o para formatar o m√™s
function formatarMes(mes, ano) {
  const meses = [
    'Janeiro', 'Fevereiro', 'Mar√ßo', 'Abril', 'Maio', 'Junho',
    'Julho', 'Agosto', 'Setembro', 'Outubro', 'Novembro', 'Dezembro'
  ];
  return `${meses[mes - 1]}/${ano}`;
}

// Fun√ß√£o para truncar texto longo
function truncarTexto(texto, limite = 40) {
  if (texto.length <= limite) return texto;
  return texto.substring(0, limite) + '...';
}

try {
  // Valida√ß√£o dos dados de entrada
  if (!inputData || !inputData.deputado || !inputData.periodoDespesa || !inputData.resumoDespesasMes) {
    throw new Error('Dados de entrada incompletos');
  }

  const { deputado, periodoDespesa, resumoDespesasMes } = inputData;

  // Valida√ß√£o dos campos obrigat√≥rios do deputado
  if (!deputado.nome || !deputado.partido || !deputado.uf) {
    throw new Error('Dados do deputado incompletos');
  }

  // Valida√ß√£o do per√≠odo
  if (!periodoDespesa.ano || !periodoDespesa.mes) {
    throw new Error('Per√≠odo de despesa inv√°lido');
  }

  // Valida√ß√£o do resumo
  if (resumoDespesasMes.totalGeral === undefined || !Array.isArray(resumoDespesasMes.despesasPorTipo)) {
    throw new Error('Resumo de despesas inv√°lido');
  }

  // Montagem da mensagem
  let mensagem = '';

  // Cabe√ßalho com dados do deputado
  mensagem += `üìä *RELAT√ìRIO DE DESPESAS PARLAMENTARES*\n\n`;
  mensagem += `üë§ *Deputado(a):* ${deputado.nome}\n`;
  mensagem += `üèõÔ∏è *Partido/UF:* ${deputado.partido}/${deputado.uf}\n`;
  mensagem += `üìÖ *Per√≠odo:* ${formatarMes(periodoDespesa.mes, periodoDespesa.ano)}\n`;

  // Adicionar datas espec√≠ficas se dispon√≠veis
  if (periodoDespesa.inicio && periodoDespesa.fim) {
    const dataInicio = new Date(periodoDespesa.inicio).toLocaleDateString('pt-BR');
    const dataFim = new Date(periodoDespesa.fim).toLocaleDateString('pt-BR');
    mensagem += `üìÜ *De:* ${dataInicio} *at√©* ${dataFim}\n`;
  }

  mensagem += `\n`;

  // Resumo geral
  mensagem += `üí∞ *RESUMO GERAL*\n`;
  mensagem += `‚îú Total Gasto: *${formatarValor(resumoDespesasMes.totalGeral)}*\n`;
  mensagem += `‚îî Quantidade de Despesas: *${formatarNumero(resumoDespesasMes.quantidadeDespesas)}*\n\n`;

  // Despesas por tipo
  if (resumoDespesasMes.despesasPorTipo && resumoDespesasMes.despesasPorTipo.length > 0) {
    mensagem += `üìã *DETALHAMENTO POR CATEGORIA*\n\n`;

    // Ordenar por valor total (maior para menor)
    const despesasOrdenadas = resumoDespesasMes.despesasPorTipo
      .sort((a, b) => b.valorTotal - a.valorTotal);

    despesasOrdenadas.forEach((despesa, index) => {
      const isUltimo = index === despesasOrdenadas.length - 1;
      const prefixo = isUltimo ? '‚îî' : '‚îú';

      // Calcular percentual do total
      const percentual = ((despesa.valorTotal / resumoDespesasMes.totalGeral) * 100).toFixed(1);

      mensagem += `${prefixo} *${truncarTexto(despesa.tipo)}*\n`;
      mensagem += `${isUltimo ? '  ' : '‚îÇ '} üíµ Valor: ${formatarValor(despesa.valorTotal)} (${percentual}%)\n`;
      mensagem += `${isUltimo ? '  ' : '‚îÇ '} üìä Qtd: ${formatarNumero(despesa.quantidade)} despesas\n`;

      if (!isUltimo) {
        mensagem += `‚îÇ\n`;
      }
    });
  }

  // Rodap√© informativo
  mensagem += `\n\n`;
  mensagem += `‚ÑπÔ∏è *Fonte:* C√¢mara dos Deputados\n`;
  mensagem += `ü§ñ *Gerado em:* ${new Date().toLocaleString('pt-BR', { 
    timeZone: 'America/Sao_Paulo' 
  })}`;

  // Retornar o resultado formatado
  return {
    mensagem: mensagem,
    sucesso: true,
    dadosProcessados: {
      deputado: deputado.nome,
      periodo: formatarMes(periodoDespesa.mes, periodoDespesa.ano),
      totalDespesas: resumoDespesasMes.quantidadeDespesas,
      valorTotal: resumoDespesasMes.totalGeral,
      categorias: resumoDespesasMes.despesasPorTipo?.length || 0
    }
  };

} catch (error) {
